mapper.select:
SELECT DISTINCT lta.id, lta.title,lta.version versionName,lta.end_time,lta.start_time, lta. STATUS,lta.create_id,IF (lta.creator_channel_id = 0,'所有渠道',lc. NAME) channelName,lta.creator_channel_id, ltc.classification_name typeName,lta.create_date,lta.zip_url FROM laun_theme_administration lta LEFT JOIN laun_channel lc ON lta.creator_channel_id = lc.id LEFT JOIN laun_theme_classification ltc ON lta.type_id = ltc.id WHERE ( (lc.channel_status = 0 OR lc.channel_status is null)
AND lta.`status` = #{status}
AND lta.`status` in (0,1,2,3)
AND lta.type_id = #{type}
AND lta.version = #{version}
AND lta.creator_channel_id = #{channel}
And lta.title LIKE #{title}
)
OR ((lc.channel_status = 0 OR lc.channel_status is null) AND lta.creator_channel_id = 0
AND lta.`status` = #{status}
AND lta.`status` in (1,2,3)
AND lta.type_id = #{type}
AND lta.version = #{version}
And lta.title LIKE #{title}
)
ORDER BY lta.create_date DESC
